function Classio(data = []){

	//constructor
	this.data = data;

	this.hide = function(className){
		var classList = $('.' + className).attr('class');
		var data = classList.split(' ');
		for(var i = 0; i < data.length; i++){
			if(data[i] === className)
				$('.' + className) . hide();
		}

		var idName = classList;
		id = $('#' + idName) . attr('id');
		if(idName === id)
			$('#' + idName) . hide();
	}
}